        {
            result = await groupService.DeleteGroupAsync(delGroupId);
        }
        if (result)
        {
            await JS.InvokeVoidAsync("showAlert", "success", languageContainer.Keys["dialog:delete_success"]);
            groups = await groupService.ViewGroupAsync();
        }
        else
